package pencil.oauth2service;import org.springframework.web.bind.annotation.*;@RestControllerpublic class ServiceController {    @RequestMapping(value = "/login", method = RequestMethod.GET)    public String login() {        return "login";    }    @RequestMapping(value = "/api/hello", method = RequestMethod.GET)    public String hello(String code, String state) {        String hello = String.format("hello code=%s,state=%s", code, state);        System.out.println(hello);        return hello;    }    @RequestMapping(value = "/api/welcome", method = RequestMethod.GET)    public String welcome(String code, String state) {        String hello = String.format("welcome code=%s,state=%s", code, state);        System.out.println(hello);        return hello;    }}